import { Component, OnInit } from '@angular/core';
import { AuthService } from './../../../src/services/auth/auth.service'; // Import the AuthService

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.scss']
})
export class NavigationComponent implements OnInit {
  currentUser: string= ''; // Replace 'any' with the actual type of your user data

  constructor(public authService: AuthService) { }

  ngOnInit(): void {
    this.authService.getCurrentUser().subscribe(
      (user: any) => this.currentUser = user,
      (error: Error) => console.error(error)
    );
  }

  onLogout(): void {
    this.authService.logout();
  }

}


<!-- Dark Themed Navigation Bar -->
<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
    <a class="navbar-brand" href="#">Your Brand</a>
    <div class="collapse navbar-collapse">
        <ul class="navbar-nav ms-auto">
            <li class="nav-item">
                <a class="nav-link" routerLink="/visits" routerLinkActive="active">Visits</a>
            </li>
            <!-- Other menu links -->
    <!-- <li *ngIf="userType === 'receptionist'">
        <a routerLink="/patient">Patient</a>
      </li> -->
            <li class="nav-item">
                <!-- <a class="nav-link" [routerLink]="['/login']">Login</a> -->
                <a class="nav-link" *ngIf="!authService.isLoggedIn()" routerLink="/login">Login</a>
                <span class="white" *ngIf="authService.isLoggedIn()">
                    <span  class="white">Welcome, {{ currentUser}}</span>
                  <a class="nav-link" routerLink="/logout" (click)="onLogout()">   Logout</a>
                </span>
                <!-- Include menu link for patient to be displayed only for Receptionist and press enter  -->
            </li>
        </ul>
    </div>
</nav>
