import { Component, OnInit } from '@angular/core';
import { PatientsService } from '../../../src/services/patients/patients.service'; // Adjust the path as necessary
 import { Patient } from '../../../src/models/patient'; // Adjust the path as necessary
 import { AuthService } from 'src/services/auth/auth.service';
// import { get } from 'http';

 interface Visit {
  condition: any;
  
    id: number;
    date: string;
    time: string;
    treatment: string;
    // Add other properties as needed
    prescriptions: [
      {
        medicineName: string;
                dosage: string;
                medicineCount: number;
                afterMeal: boolean;
              durationDate: string;
              advice: string
      }
    ]
  }
  

@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.scss'],
 
})
export class PatientComponent implements OnInit {
  patientNotFound: boolean = false;
  patientId: string = ''; // Replace with the actual patient ID
  mobileNumber: string = ''; // Initialize the property
  patient: Patient = {} as Patient;
  vpatient = {
    visits: [] as Visit[]
  };
  errorMessage = '';
  
  visit:any[] = []; // Replace 'any' with the actual type of your visit data
 // patient: any; // Replace 'any' with the actual type of your patient data
  doctor: any; // Replace 'any' with the actual type of your doctor data
  user: any;
   currentUser: string = '';

  constructor(private patientService: PatientsService, private authservice:AuthService) {}

  ngOnInit(): void {
     this.patientId = '3'; // Replace with the actual patient ID
     this.getuserinfo();
   
    } 
   
    getuserinfo(){
      this.patientService.getPatient(this.patientId).subscribe( // here we need to get the patient detials
        (user: any) => {
          this.user = user;
          this.vpatient.visits = user.visits;
        
        // this.visitsService.getVisits(patientId).subscribe(
        //   visit => {
        //     this.visit = visit;
            this.patientService.getPatient(this.patientId).subscribe(
              patient => this.patient = patient,
              
              error =>{
                errorMessage: 'Error fetching patient details';
                console.error(error)
              } 
            );
            this.authservice.getCurrentUser()(
              (user1: any) => this.currentUser = user1,
              (error: Error) => {
                errorMessage: 'Get user data service error';
                console.error(error)
              }
            );
          
          },
          error =>{
            errorMessage: 'Get patient data service error';
            console.error(error)
          } 
        );

    }

  searchPatient(): void {
    
    if (this.mobileNumber) {
    this.patientService.searchPatientByMobile(this.mobileNumber).subscribe(
      (patient: Patient) =>{
        if(patient){
         this.patientNotFound = true;
          this.patient = patient;
          this.patientId =`${patient.id}`;

          this.getuserinfo();
        } else {
          this.patientNotFound = false;
          console.error('Patient not found');
      } 
      (error: Error) => {
        this.patientNotFound = false;
        console.error('Error fetching patient:', error)
      }
    }
    );
  } else {
    console.error('Mobile number is not provided');
  }
  }

}
<div class="container mt-4">
    <div class="row">
      <div class="col-md-12">
        <div class="input-group mb-3">
    <input [(ngModel)]="mobileNumber" class="form-control" placeholder="Enter mobile number" />
    <button class="btn btn-primary" (click)="searchPatient()">Search</button>
  </div>
  <div *ngIf="errorMessage" class="alert alert-danger">{{errorMessage}}</div>
  <div *ngIf="patientNotFound">
  <div class="container mt-4">
    <div class="row">
      <div class="col-md-12">
        <button class="btn btn-primary mb-3" [routerLink]="['/register-patient']">
          Register New Patient
        </button>

        </div>
        </div>
        </div>
    </div>
  <div *ngIf="patient">
    <div  class="card mb-4">
        <div class="card-body">
    <h2 class="card-title">Patient Details</h2>
    <p class="card-text" >ID: {{ patient.id }}</p>
    <p class="card-text">Name: {{ patient.name }}</p>
    <p class="card-text">Mobile Number: {{ patient.mobileNumber }}</p>
    <!-- Display other patient details as necessary -->
    <button class="btn btn-secondary mt-3" [routerLink]="['/add-visit', patient.id]">Add Visit</button>
  <!-- </div> -->
</div>
</div>
</div>
  <div class="container">
    <h2>Visit Details</h2>
    <div class="row">
       <p><strong>Doctor Name:</strong>{{this.currentUser}} </p>
        <div class="col-md-3" *ngFor="let visit of vpatient.visits">
        
            <div class="card">
                <div class="card-body">
                    <h5 class="card-title"></h5>
                    <p class="card-text"></p>
                   
                    <p><strong>Patient Name:</strong> {{this.user.name}}</p>
                    <p><strong>Condition:</strong>{{visit.condition}} </p>
                    <p><strong>Treatment:</strong> {{visit.treatment}}</p>
                    <!-- <button class="btn btn-primary" >View Details</button> -->
                </div>
            </div>
        </div>
    </div>

     <br>
     <br>           
   
  
    <h3>Prescribed Medicine</h3>
    <table class="table">
      <thead>
        <tr>
          <th>Medicine Name</th>
          <th>Dosage</th>
          <th>After Meal</th>
          <th>Duration Date</th>
          <th>Advice</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let visit of vpatient.visits">
          <ng-container *ngFor="let medicine of visit.prescriptions">
        
          <td>{{medicine.medicineName}}</td>
          <td>{{medicine.dosage}}</td>
          <td>{{medicine.afterMeal ? 'Yes' : 'No'}}</td>
          <td>{{medicine.durationDate | date:'MMM dd, yyyy'}}</td>
          <td>{{medicine.advice}}</td>
    
      </ng-container>
    </tr>
      </tbody>
    </table>
</div>
</div>
</div>
</div>
