import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { UsersService } from 'src/services/users/users.service';

@Component({
  selector: 'app-patient-registration',
  templateUrl: './patient-registration.component.html',
  styleUrls: ['./patient-registration.component.css']
})
export class PatientRegistrationComponent  implements OnInit{
  registrationForm: FormGroup;
  newPatient: any = {
    name: '',
    mobileNumber: ''
  };

  constructor(private userService: UserService,private http: HttpClient, private router: Router, private fb:FormBuilder) {
    this.registrationForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(2)]],
      mobileNumber: ['', [Validators.required, Validators.pattern('^[0-9]{10}$')]]
    });

  }
  ngOnInit(): void {}

  registerPatient(): void {
    if (this.registrationForm.valid) {
    const patientData = {
      name: this.newPatient.name,
      mobileNumber: this.newPatient.mobileNumber,
      type: 'patient',
      password: 'pwd'
    };

    this.userService.createUser(patientData).subscribe(
      (response: any) => {
        console.log('Patient registered successfully:', response);
        this.router.navigate(['/patients']);
      },
      (error: any) => {
        console.error('Error registering patient:', error);
      }
    );
  }
  }
}
